import React from "react";
import AppContext from '../../context'
import styles from "./Form.module.scss";
import Input from "../Input/Input";
import Button from '../Button/Button';
import Title from '../Title/Title';
import Radio from './FormRadio'

const types = {
  twitter: 'twitter',
  article: 'article',
  note: 'note',
}
const descriptions = {
  twitter: 'favorite Twitter account',
  article: 'Article',
  note: 'Note',
}

class Form extends React.Component{
  state = {
    activeOption: types.twitter,
    title: '',
    link: '',
    image: '',
    description: '',
  }

  handleRadioBtnChange = (type) => {
    this.setState({
      activeOption: type,
    })
  }

  handleInputChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
  };


  render(){

    const {activeOption} = this.state

    return (
      <AppContext.Consumer>
      {(context) => (
        <div className={styles.wrapper}>
        <Title>Add new {descriptions[activeOption]}</Title>
        <form autoComplete="off" className={styles.form}
         onSubmit={context.addItem}>
           <div className={styles.formOptions}>
           <Radio
            id={types.twitter}
            checked={activeOption === types.twitter}
            changeFn={() => this.handleRadioBtnChange(types.twitter)}
            >Twitter
            </Radio>
            <Radio
            id={types.article}
            checked={activeOption === types.article}
            changeFn={() => this.handleRadioBtnChange(types.article)}
            >Article
            </Radio>
            <Radio
            id={types.note}
            checked={activeOption === types.note}
            changeFn={() => this.handleRadioBtnChange(types.note)}
            >Note
            </Radio>
            </div>
          <Input 
            onChange={this.handleInputChange}
            value={this.state.title}
            name="title"
            label={activeOption === types.twitter ? 'Twitter Name' : 'Title'}
            maxLength={30}
          />
          {activeOption !== types.note ? (
          <Input 
           onChange={this.handleInputChange}
           value={this.state.link}
            name="link"
            label={activeOption === types.twitter ? 'Twitter Link' : 'Link' }
          />) : null}

          {activeOption === types.twitter ? 
          <Input 
           onChange={this.handleInputChange}
            value={this.state.image}
            name="image"
            label="Image"
          /> : null}
          <Input 
            onChange={this.handleInputChange}
            value={this.state.description}
            tag="textarea"
            name="description"
            label="Description"
          />
          <Button>add new item</Button>
        </form>
      </div>
      )}
    </AppContext.Consumer>
    )
  }
}



export default Form;
