import React from "react";
import styles from "./Form.module.scss";
import Input from "../Input/Input";
import Button from '../Button/Button';
import Title from '../Title/Title';
import Radio from './FormRadio'

const types = {
  twitter: 'twitter',
  article: 'article',
  note: 'none',
}
const descriptions = {
  twitter: 'favorite Twitter account',
  article: 'Article',
  note: 'Note',
}

class Form extends React.Component{
  state = {
    activeOption: types.twitter
  }

  handleRadioBtnChange = (type) => {
    this.setState({
      activeOption: type,
    })
  }

  render(){
    return (
      <div className={styles.wrapper}>
      <Title>Add new {descriptions[this.state.activeOption]}</Title>
      <form autoComplete="off" className={styles.form}
       onSubmit={this.props.submitFn}>
         <div className={styles.formOptions}>
         <Radio
          id={types.twitter}
          checked={this.state.activeOption === types.twitter}
          changeFn={() => this.handleRadioBtnChange(types.twitter)}
          >Twitter
          </Radio>
          <Radio
          id={types.article}
          checked={this.state.activeOption === types.article}
          changeFn={() => this.handleRadioBtnChange(types.article)}
          >Article
          </Radio>
          <Radio
          id={types.note}
          checked={this.state.activeOption === types.note}
          changeFn={() => this.handleRadioBtnChange(types.note)}
          >Note
          </Radio>
          </div>
        <Input
          name="name"
          label="Name"
          maxLength={30}
        />
        <Input
          name="link"
          label="Twitter link"
        />
        <Input
          name="image"
          label="Image"
        />
        <Input
          tag="textarea"
          name="description"
          label="Description"
        />
        <Button>add new item</Button>
      </form>
    </div>
    )
  }
}



export default Form;
