import React from "react";
import "./index.css";
import {BrowserRouter, Route, Switch} from 'react-router-dom'
import TwittersViev from '../TwittersView/TwittersView'
import NotesView from '../NotesView/NotesView'
import ArticleView from '../ArticleView/ArticleView'
import Header from '../../components/Header/Header'
import Modal from '../../components/Modal/Modal'

const initialStateItems = [
  {
    image: "https://i.kym-cdn.com/entries/icons/facebook/000/031/003/cover3.jpg",
    name: "Dan Abramov",
    description: "React core member",
    twitterLink: "https://twitter.com/dan_abramov"
  }
];

class Root extends React.Component {
  state = {
    items: [...initialStateItems],
    isModalOpen: false,
  };

  addItem = e => {
    e.preventDefault();


    const newItem = {
      name: e.target[0].value,
      twitterLink: e.target[1].value,
      image: e.target[2].value,
      description: e.target[3].value
    };

    this.setState(prevState => ({
      items: [...prevState.items, newItem]
    }));

    e.target.reset();
  };

    openModal = () => {
      this.setState({
        isModalOpen: true,
      })
    }

  render() {
    const {isModalOpen} = this.state;

    return (
      <BrowserRouter>
        <>
        <Header openModalFn={this.openModal}/>
        <h1>Hello Wordl</h1>
        <Switch>
        <Route exact path="/" component = {TwittersViev} />
        <Route path="/articles" component = {ArticleView} />
        <Route path="/notes" component={NotesView} />    
        </Switch>
        {isModalOpen && <Modal /> } 
        </>
      </BrowserRouter>
    );
  }
}

export default Root;
