import React from 'react'


class MyComponent extends React.Component{
  state = {
    text:'test state',
  }


  handleChange = (event) => {
    console.log(event)
    this.setState({text: event.target.value});
  }

  render(){
    return(
      <>
        <input
         placeholder="your text"
         onChange={this.handleChange}
         />
        <h1>{this.state.text}</h1>
      </>
    )
  }
}

export default MyComponent
